@file:Suppress("SameParameterValue", "UnnecessaryVariable", "UNUSED_VARIABLE")

import java.io.File

/**
 * @link:
 * stepik: https://stepik.org/lesson/240255/step/3?unit=212601
 * rosalind: http://rosalind.info/problems/ba3a/
 *
String Reconstruction practice problem

 */

fun main() {

    val dnaString = ""
    val kmerLength = 100

    val outputMessagesFilePath = "stringsComposition.txt"

    val outFile = File(outputMessagesFilePath)
    val writer = outFile.bufferedWriter()

    val stringList : MutableList<String> = mutableListOf()

    for (i in 0 until dnaString.length-kmerLength+1) {
        stringList.add(dnaString.substring(i, i+kmerLength))
        writer.write(dnaString.substring(i, i+kmerLength))
        writer.write("\n")
    }
    //println(stringList)
    writer.close()

}




