    /**
     * test scanDnaStringForPeptideEncoding
     */
    @Test
    @DisplayName("scanDnaStringForPeptideEncoding - 01")
    fun testScanDnaStringForPeptideEncoding01() {
        val dnaString = "ATGGCCATGGCCCCCAGAACTGAGATCAATAGTACCCGTATTAACGGGTGA"
        val peptide = "MA"
        val result = scanDnaStringForPeptideEncoding(dnaString, peptide)

        val reverseDnaString = reverseComplement(dnaString)
        val peptideR = "MA"
        val resultR = scanDnaStringForPeptideEncoding(reverseDnaString, peptide, true)


        val expectedLines = """
        ATGGCC
        GGCCAT
        ATGGCC
    """.trimIndent()
        val reader = expectedLines.reader()
        val lines = reader.readLines().sorted()

        val total = result + resultR
        //total.forEach { println(it) }
        assertEquals(lines, total.sorted())

    }


