import algorithms.reverseComplement
import org.junit.jupiter.api.Assertions.assertEquals
import org.junit.jupiter.api.DisplayName
import org.junit.jupiter.api.Test

/**
 *  1.3 Some Hidden Messages are More Surprising than Others

Reverse Complement Problem: Find the reverse complement of a DNA string.

Input: A DNA string Pattern.
Output: Patternrc , the reverse complement of Pattern.


See also:
rosalind: http://rosalind.info/problems/ba1c/
 */

internal class S01c03p02ReverseComplementTest {

    @Test
    @DisplayName("ReverseDNA - test with DNA strip supplied by class")
    fun reverseDnaSample() {

        val sampleString = "AAAACCCGGT"

        val expectedResult = "ACCGGGTTTT"

        val returnedValue = reverseComplement(sampleString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

    @Test
    @DisplayName("ReverseDNA - test with single letter")
    fun reverseDnaSampleSingleLetter() {

        val sampleString = "A"

        val expectedResult = "T"

        val returnedValue = reverseComplement(sampleString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

    @Test
    @DisplayName("ReverseDNA - test with big Class string")
    fun reverseClassString() {

    val classString = """
        
    """.trimIndent()

        val expectedResult = """
        
    """.trimIndent()

        val returnedValue = reverseComplement(classString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

}