import org.junit.jupiter.api.AfterEach
import org.junit.jupiter.api.BeforeEach
import org.junit.jupiter.api.Test

import org.junit.jupiter.api.Assertions.*
import org.junit.jupiter.api.DisplayName
import util.reverseComplement

internal class S01c03p02Test {

    @BeforeEach
    fun setUp() {
    }

    @AfterEach
    fun tearDown() {
    }

    @Test
    @DisplayName("ReverseDNA - test with DNA strip supplied by class")
    fun reverseDnaSample() {

        val sampleString = "AAAACCCGGT"

        val expectedResult = "ACCGGGTTTT"

        val returnedValue = reverseComplement(sampleString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

    @Test
    @DisplayName("ReverseDNA - test with single letter")
    fun reverseDnaSampleSingleLetter() {

        val sampleString = "A"

        val expectedResult = "T"

        val returnedValue = reverseComplement(sampleString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

    @Test
    @DisplayName("ReverseDNA - test with big Class string")
    fun reverseClassString() {

    val classString = """
        
    """.trimIndent()

        val expectedResult = """
        
    """.trimIndent()

        val returnedValue = reverseComplement(classString)

        assertEquals(returnedValue.trimEnd(), expectedResult)

    }

}